import React, { useState, useEffect } from 'react';
import { Button } from '@material-ui/core';
import { useParams } from 'react-router-dom';
import api from '../services/api.ts';

const getUsers = async () => {
  const response = await api.get('/users');
  return response.data;
};

interface User {
  _id: string;
  name: string;
  email: string;
}

const MissionCollaborators: React.FC = () => {
  const { missionId, missionName } = useParams<{ missionId: string, missionName: string }>();
  const [users, setUsers] = useState<User[]>([]);
const [selectedUsers, setSelectedUsers] = useState<User[]>([]);

const handleAddToMission = async (userId: string) => {
  try {
    await api.post(`/missions/${missionId}/collaborators`, { userId });
    const user = users.find(u => u._id === userId);
    if (user) {
      setSelectedUsers([...selectedUsers, user]);
      setUsers(users.filter(u => u._id !== userId));
    }
  } catch (error) {
    console.error('Error adding collaborator:', error);
  }
};

const handleRemoveFromMission = async (userId: string) => {
  try {
    await api.delete(`/missions/${missionId}/collaborators/${userId}`);
    const user = selectedUsers.find(u => u._id === userId);
    if (user) {
      setUsers([...users, user]);
      setSelectedUsers(selectedUsers.filter(u => u._id !== userId));
    }
  } catch (error) {
    console.error('Error removing collaborator:', error);
  }
};

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const data = await getUsers();
        setUsers(data);
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    fetchUsers();
  }, []);

  return (
    <div>
      <h1>Mission Collaborators</h1>
      <h2>{missionName}</h2>
      <p>Collaborators for Mission ID: {missionId}</p>

      <h3>All Unselected Users</h3>
      <ul>
        {users.map(user => (
          <li key={user._id}>
            {user.name} - {user.email}
            <Button 
              variant="contained" 
              color="primary" 
              onClick={() => handleAddToMission(user._id)}
              style={{ marginLeft: '10px' }}
            >
              Add to Mission
            </Button>
          </li>
        ))}
      </ul>

      <h3>Selected Users</h3>
      <ul>
        {selectedUsers.map(user => (
          <li key={user._id}>
            {user.name} - {user.email}
            <Button 
              variant="contained" 
              color="secondary" 
              onClick={() => handleRemoveFromMission(user._id)}
              style={{ marginLeft: '10px' }}
            >
              Drop from Mission
            </Button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MissionCollaborators;
